function W = coswin(n, id, od)% COSWIN - makes a circular window with cosine taperif (od)> n/2	error('radii must be < half the window diameter');endW = ones(n);r2rad = (pi/2).*(1/(od-id)); % circle radius to radians [r, c] = find(W);	% get x and y coords.r0 = r - n/2;		% ... and shift re. originc0 = c - n/2;h = sqrt(r0.^2 + c0.^2);points = find( (h>id) & (h<=od) );vals =  cos( (h(points)-id) .* r2rad);points2 = find(h>od);vals2 = 0.*h(points2);points = [points; points2];vals = [vals; vals2];%keyboardfor k=1:size(points)		W( r(points(k)), c(points(k)) ) = vals(k);end